import { Request, Response } from 'express';
import TeamService from '../services/TeamService';

export default class TeamController {
  constructor(private teamService = new TeamService()) {}

  public findAllTeam = async (req: Request, res: Response) => {
    const result = await this.teamService.findAllTeam();
    return res.status(200).json(result);
  };

  public findOneTeam = async (req: Request, res: Response) => {
    const { id } = req.params;
    const result = await this.teamService.findOneTeam(Number(id));
    return res.status(200).json(result);
  };
}

import { ModelStatic } from 'sequelize';
import Team from '../database/models/TeamModel';
import { ITeam } from '../intefaces/ITeam';

export default class TeamService {
  private model: ModelStatic<Team> = Team;

  public async findAllTeam(): Promise<ITeam[]> {
    const result = await this.model.findAll();
    return result;
  }

  public async findOneTeam(id: number): Promise<ITeam | null> {
    const result = await this.model.findOne({ where: { id } });
    return result;
  }
}

