import { Request, Response } from 'express';
import * as bcrypt from 'bcryptjs';
import { generateToken } from '../auth/authToken';
import UserService from '../services/UserService';
import { ILogin } from '../intefaces/ILogin';

export default class UserController {
  constructor(private userService = new UserService()) {}

  public findOneLogin = async (req: Request, res: Response) => {
    const { email, password } = req.body;
    // const role = req.payload;
    // console.log(role);
    const result = await this.userService.findOneLogin(email);
    const validadeSenha = bcrypt.compareSync(password, result?.password || '');
    if (!validadeSenha) {
      return res.status(401).json({ message: 'Invalid email or password' });
    }
    const token = generateToken(result as unknown as ILogin);
    return res.status(200).json({ token });
  };

  public findOneRole = async (req: Request, res: Response) => {
    const { role } = req.body;
    const result = await this.userService.findOneRole(role);
    return res.status(200).json({ role: result?.role });
  };
}

import * as jwt from 'jsonwebtoken';
import { ILogin } from '../intefaces/ILogin';

const secret = process.env.JWT_SECRET || 'secret';

export const generateToken = (payload: ILogin) => jwt.sign({ payload }, secret, {
  algorithm: 'HS256',
  expiresIn: '7d',
});

export const verifyToken = (token: string) => jwt.verify(token, secret);

import { ModelStatic } from 'sequelize';
import User from '../database/models/UsersModel';
import { ILogin } from '../intefaces/ILogin';
import { IUser } from '../intefaces/IUser';

class UserService {
  private model: ModelStatic<User> = User;

  public async findOneLogin(email: string): Promise<ILogin | null> {
    const result = await this.model.findOne({ where: { email } });
    return result;
  }

  public async findOneRole(role: string): Promise<IUser | null> {
    const result = await this.model.findOne({ where: { role } });
    return result;
  }
}

export default UserService;

import { Response, Request, NextFunction } from 'express';

const verificaLogin = async (req: Request, res: Response, next: NextFunction) => {
  const { email, password } = req.body;
  if (!email || !password) {
    return res.status(400).json({ message: 'All fields must be filled' });
  }
  if (password.length < 6) {
    return res.status(401).json({ message: 'Invalid email or password' });
  }
  const regex = /\S+@\S+\.\S+/;
  if (!regex.test(email)) {
    return res.status(401).json({ message: 'Invalid email or password' });
  }
  next();
};

export default {
  verificaLogin,
};

import { Response, Request, NextFunction } from 'express';
import { verifyToken } from '../auth/authToken';
import { IPayload } from '../intefaces/IUser';

const verificaAuthToken = async (req: Request, res: Response, next: NextFunction) => {
  const { authorization } = req.headers;

  if (!authorization) {
    return res.status(401).json({ message: 'Token not found' });
  }
  if (authorization.length < 16) {
    return res.status(401).json({ message: 'Token must be a valid token' });
  }
  const payload = verifyToken(authorization) as unknown as IPayload;
  req.body.role = payload.payload.role;

  if (!payload) {
    return res.status(401).json({ message: 'Token must be a valid token' });
  }
  next();
};

export default verificaAuthToken;

import { Router } from 'express';

import MatchController from '../controllers/MatchesController';
import verificaAuthToken from '../middleware/validadeAuthToken';

const matchController = new MatchController();

const router = Router();

router.post('/', verificaAuthToken, matchController.createMatch);
router.get('/', matchController.findAllMatch);
router.patch('/:id', verificaAuthToken, matchController.updateMatchInProgress);
router.patch('/:id/finish', verificaAuthToken, matchController.finishMatch);

export default router;

import { Request, Response } from 'express';
import MatchService from '../services/MatchesService';
import TeamService from '../services/TeamService';

export default class MatchController {
  constructor(
    private matchService = new MatchService(),
    private teamService = new TeamService(),
  ) {}

  public findAllMatch = async (req: Request, res: Response) => {
    const { inProgress } = req.query;
    if (inProgress === undefined) {
      const result = await this.matchService.findAllMatch();
      return res.status(200).json(result);
    }
    const progress = await this.matchService.findInProgress(inProgress === 'true');
    return res.status(200).json(progress);
  };

  public finishMatch = async (req: Request, res: Response) => {
    const { id } = req.params;
    await this.matchService.finishMatch(Number(id));
    return res.status(200).json({ message: 'Finished' });
  };

  public updateMatchInProgress = async (req: Request, res: Response) => {
    const { id } = req.params;
    const { homeTeamGoals, awayTeamGoals } = req.body;
    await this.matchService.updateMatchInProgress(Number(id), homeTeamGoals, awayTeamGoals);
    return res.status(200).json({ message: 'Updated' });
  };

  public createMatch = async (req: Request, res: Response) => {
    const { homeTeamId, homeTeamGoals, awayTeamId, awayTeamGoals } = req.body;

    if (homeTeamId === awayTeamId) {
      return res.status(422).json(
        { message: 'It is not possible to create a match with two equal teams' },
      );
    }
    const homeTeam = await this.teamService.findOneTeam(homeTeamId);
    const awayTeam = await this.teamService.findOneTeam(awayTeamId);
    if (!homeTeam || !awayTeam) {
      return res.status(404)
        .json({ message: 'There is no team with such id!' });
    }

    const team = await this.matchService
      .createMatch(homeTeamId, homeTeamGoals, awayTeamId, awayTeamGoals);
    return res.status(201).json(team);
  };
}

import { ModelStatic } from 'sequelize';
import Match from '../database/models/MatchesModels';
import Team from '../database/models/TeamModel';

export default class MatchService {
  private model: ModelStatic<Match> = Match;

  public async findAllMatch(): Promise<Match[]> {
    const result = await this.model.findAll({
      include: [
        { model: Team, as: 'homeTeam', attributes: { exclude: ['id'] } },
        { model: Team, as: 'awayTeam', attributes: { exclude: ['id'] } },
      ],
    });
    return result;
  }

  public async findInProgress(inProgress: boolean | undefined): Promise<Match[]> {
    const result = await this.model.findAll({
      include: [
        { model: Team, as: 'homeTeam', attributes: { exclude: ['id'] } },
        { model: Team, as: 'awayTeam', attributes: { exclude: ['id'] } },
      ],
      where: { inProgress },
    });
    return result;
  }

  public async finishMatch(id: number) {
    const result = await this.model.update(
      { inProgress: false },
      { where: { id } },
    );
    return result;
  }

  public async updateMatchInProgress(id: number, homeTeamGoals: number, awayTeamGoals: number) {
    const result = await this.model.update(
      { homeTeamGoals, awayTeamGoals },
      { where: { id } },
    );
    return result;
  }

  public async findIdMatch(id: number) {
    const result = await this.model.findByPk(id);
    return result;
  }

  public async createMatch(
    homeTeamId: number,
    homeTeamGoals: number,
    awayTeamId: number,
    awayTeamGoals: number,
  ) {
    const { id } = await this.model.create({
      homeTeamId,
      homeTeamGoals,
      awayTeamId,
      awayTeamGoals,
      inProgress: true });
    return { id, homeTeamId, homeTeamGoals, awayTeamId, awayTeamGoals, inProgress: true };
  }
}

import { Router } from 'express';

import LeaderboardController from '../controllers/LeaderboardController';

const router = Router();

const leaderboardController = new LeaderboardController();

router.get('/home', leaderboardController.homeTeamsPoints);

export default router;

import { Request, Response } from 'express';
import LeaderboardService from '../services/LeaderboardService';

export default class LeaderboardController {
  constructor(private leaderboardService = new LeaderboardService()) {}

  public homeTeamsPoints = async (req: Request, res: Response) => {
    const result = await this.leaderboardService.homeTeamsPoints();
    return res.status(200).json(result);
  };
}

import { ModelStatic } from 'sequelize';
import Match from '../database/models/MatchesModels';
import Team from '../database/models/TeamModel';
import { ITeamMatch } from '../intefaces/ILeaderboard';
import homeService from './utils/homeTeamUtil';
import oredenaMatches from './utils/sort';

export default class LeaderboardService {
  private model: ModelStatic<Match> = Match;

  public async homeTeamsPoints() {
    const home = await this.model.findAll({
      include: [{ model: Team, as: 'homeTeam', attributes: { exclude: ['id'] } }],
      where: { inProgress: false },
    });
    const team = homeService(home as unknown as ITeamMatch[]);
    const result = oredenaMatches(team);
    return result;
  }
}

import { ITeamMatch, ILeaderboard } from '../../intefaces/ILeaderboard';

const homeTotalPoints = (matches: ITeamMatch[]) => {
  let total = 0;
  matches.forEach((jogo) => {
    if (jogo.homeTeamGoals > jogo.awayTeamGoals) {
      total += 3;
    } else if (jogo.homeTeamGoals === jogo.awayTeamGoals) {
      total += 1;
    }
  });
  return total;
};

const homeTotalVictories = (matches: ITeamMatch[]) => {
  let total = 0;
  matches.forEach((jogo) => {
    if (jogo.homeTeamGoals > jogo.awayTeamGoals) {
      total += 1;
    }
  });
  return total;
};

const homeTotalDraws = (matches: ITeamMatch[]) => {
  let total = 0;
  matches.forEach((jogo) => {
    if (jogo.homeTeamGoals === jogo.awayTeamGoals) {
      total += 1;
    }
  });
  return total;
};

const homeTotalLosses = (matches: ITeamMatch[]) => {
  let total = 0;
  matches.forEach((jogo) => {
    if (jogo.homeTeamGoals < jogo.awayTeamGoals) {
      total += 1;
    }
  });
  return total;
};

const homeEfficiency = (matches: ITeamMatch[]) => (((homeTotalPoints(matches)) / (matches
  .length * 3)) * 100).toFixed(2);

const homeMatches = (matches: ITeamMatch[]) => {
  let casa = 0;
  let visitante = 0;
  const homePoints = matches.map((jogo) => {
    const home = {
      name: jogo.homeTeam.teamName,
      totalPoints: homeTotalPoints(matches),
      totalGames: matches.length,
      totalVictories: homeTotalVictories(matches),
      totalDraws: homeTotalDraws(matches),
      totalLosses: homeTotalLosses(matches),
      goalsFavor: casa += jogo.homeTeamGoals,
      goalsOwn: visitante += jogo.awayTeamGoals,
      goalsBalance: casa - visitante,
      efficiency: homeEfficiency(matches),
    };
    return home;
  });
  return homePoints;
};

const homeService = (matches: ITeamMatch[]) => {
  let homeTeam: ILeaderboard[] = [];
  for (let i = 1; i <= 16; i += 1) {
    const match = matches.filter((jogo) => jogo.homeTeamId === i);
    const home = homeMatches(match);
    homeTeam = [...homeTeam, home[home.length - 1]];
  }
  return homeTeam;
};

export default homeService;

import { ILeaderboard } from '../../intefaces/ILeaderboard';

const oredenaMatches = (teams: ILeaderboard[]) => teams.sort((b, a) => {
  if (a.totalPoints === b.totalPoints) {
    if (a.goalsBalance === b.goalsBalance) {
      return a.goalsFavor - b.goalsFavor;
    } return a.goalsBalance - b.goalsBalance;
  }
  return a.totalPoints - b.totalPoints;
});

export default oredenaMatches;
